import React, { Component } from "react";
import "./style.css";

export default class index extends Component {
  state = { slideIndex: 1 };
  // Next/previous controls
  plusSlides(n) {
    this.showSlides(this.state.slideIndex + n);
  }

  // Thumbnail image controls
  currentSlide(n) {
    this.showSlides(n);
  }

  showSlides(n) {
    var slideIndex = n;
    var i;
    var slides = document.getElementsByClassName("mySlides");
    var dots = document.getElementsByClassName("demo");
    var captionText = document.getElementById("caption");
    if (n > slides.length) {
      slideIndex = 1;
    }
    if (n < 1) {
      slideIndex = slides.length;
    }
    for (i = 0; i < slides.length; i++) {
    //   slides[i].style.display = "none";
    }
    // for (i = 0; i < dots.length; i++) {
    //   dots[i].className = dots[i].className.replace(" active", "");
    // }
    // slides[slideIndex - 1].style.display = "block";
    dots[slideIndex - 1].className += " active";
    captionText.innerHTML = dots[slideIndex - 1].alt;
    this.setState({ slideIndex: slideIndex });
  }
  render() {
    return (
      <section>
        <div className="detail-product__box-name">
          <div className="cps-container">
            <div className="box-name__box-product-name">
              <h1>iPhone 13 Pro Max | Chính hãng VN/A </h1>
            </div>
            <div className="box-name__box-raiting">
              <i className="fas fa-star checked" />
              <i className="fas fa-star checked" />
              <i className="fas fa-star checked" />
              <i className="fas fa-star checked" />
              <i className="fas fa-star checked" />
              &nbsp;14 đánh giá
            </div>
            <div className="box-info__box-social ">
              <div
                className="fb-like fb_iframe_widget"
                data-href="https://cellphones.com.vn/iphone-13-pro-max.html"
                data-layout="button_count"
                data-action="like"
                data-show-faces="false"
                data-share="true"
                fb-xfbml-state="rendered"
                fb-iframe-plugin-query="action=like&app_id=112980886043945&container_width=0&href=https%3A%2F%2Fcellphones.com.vn%2Fiphone-13-pro-max.html&layout=button_count&locale=vi_VN&sdk=joey&share=true&show_faces=false"
              >
                <span
                  style={{
                    verticalAlign: "bottom",
                    width: "150px",
                    height: "28px",
                  }}
                >
                  <iframe
                    name="f21f31bf5b23fc8"
                    width="1000px"
                    height="1000px"
                    data-testid="fb:like Facebook Social Plugin"
                    title="fb:like Facebook Social Plugin"
                    frameBorder={0}
                    allowTransparency="true"
                    allowFullScreen="true"
                    scrolling="no"
                    allow="encrypted-media"
                    src="https://www.facebook.com/v2.5/plugins/like.php?action=like&app_id=112980886043945&channel=https%3A%2F%2Fstaticxx.facebook.com%2Fx%2Fconnect%2Fxd_arbiter%2F%3Fversion%3D46%23cb%3Df2b66aa4f998cd%26domain%3Dcellphones.com.vn%26is_canvas%3Dfalse%26origin%3Dhttps%253A%252F%252Fcellphones.com.vn%252Ff3850371964bd8%26relation%3Dparent.parent&container_width=0&href=https%3A%2F%2Fcellphones.com.vn%2Fiphone-13-pro-max.html&layout=button_count&locale=vi_VN&sdk=joey&share=true&show_faces=false"
                    style={{
                      border: "none",
                      visibility: "visible",
                      width: "150px",
                      height: "28px",
                    }}
                    className
                  />
                </span>
              </div>
            </div>
          </div>
        </div>

        <div class="row">
          <div class="col-xs-6 col-sm-6 col-md-6 col-lg-6">
            {/* Container for the image gallery */}
            <div className="container">
              {/* Full-width images with number text */}
              <div className="mySlides">
                <div className="numbertext">1 / 6</div>
                <img
                  src="https://www.w3schools.com/howto/img_woods_wide.jpg"
                  style={{ width: "100%" }}
                ></img>
              </div>
              <div className="mySlides">
                <div className="numbertext">2 / 6</div>
                <img
                  src="https://www.w3schools.com/howto/img_5terre_wide.jpg"
                  style={{ width: "100%" }}
                />
              </div>
              <div className="mySlides">
                <div className="numbertext">3 / 6</div>
                <img
                  src="https://www.w3schools.com/howto/img_mountains_wide.jpg"
                  style={{ width: "100%" }}
                />
              </div>
              <div className="mySlides">
                <div className="numbertext">4 / 6</div>
                <img
                  src="https://www.w3schools.com/howto/img_lights_wide.jpg"
                  style={{ width: "100%" }}
                />
              </div>
              <div className="mySlides">
                <div className="numbertext">5 / 6</div>
                <img
                  src="https://www.w3schools.com/howto/img_nature_wide.jpg"
                  style={{ width: "100%" }}
                />
              </div>
              <div className="mySlides">
                <div className="numbertext">6 / 6</div>
                <img
                  src="https://www.w3schools.com/howto/img_snow_wide.jpg"
                  style={{ width: "100%" }}
                />
              </div>
              {/* Next and previous buttons */}
              <a className="prev" onclick={this.plusSlides(-1)}>
                ❮
              </a>
              <a className="next" onclick={this.plusSlides(1)}>
                ❯
              </a>
              {/* Image text */}
              <div className="caption-container">
                <p id="caption" />
              </div>
              {/* Thumbnail images */}
              <div className="row">
                <div className="column">
                  <img
                    className="demo cursor"
                    src="img_woods.jpg"
                    style={{ width: "100%" }}
                    onclick={this.currentSlide(1)}
                    alt="The Woods"
                  />
                </div>
                <div className="column">
                  <img
                    className="demo cursor"
                    src="img_5terre.jpg"
                    style={{ width: "100%" }}
                    onclick={this.currentSlide(2)}
                    alt="Cinque Terre"
                  />
                </div>
                <div className="column">
                  <img
                    className="demo cursor"
                    src="img_mountains.jpg"
                    style={{ width: "100%" }}
                    onclick={this.currentSlide(3)}
                    alt="Mountains and fjords"
                  />
                </div>
                <div className="column">
                  <img
                    className="demo cursor"
                    src="img_lights.jpg"
                    style={{ width: "100%" }}
                    onclick={this.currentSlide(4)}
                    alt="Northern Lights"
                  />
                </div>
                <div className="column">
                  <img
                    className="demo cursor"
                    src="img_nature.jpg"
                    style={{ width: "100%" }}
                    onclick={this.currentSlide(5)}
                    alt="Nature and sunrise"
                  />
                </div>
                <div className="column">
                  <img
                    className="demo cursor"
                    src="img_snow.jpg"
                    style={{ width: "100%" }}
                    onclick={this.currentSlide(6)}
                    alt="Snowy Mountains"
                  />
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>
    );
  }
}
