import React, { Component } from "react";
import "./style.css";

function importAll(r) {
  return r.keys().map(r);
}

const images = importAll(
  require.context("../../Asset/apple/iphone13", false, /\.(png|jpe?g|svg)$/)
);

const myImage = (name) => {
  let img = null;
  images.map((data) => {
    if (data.default.includes(name)) img = data.default;
  });
  return img;
};

export default class index extends Component {
  render() {
    return (
      <div>
        <div className="container">
          {/* Full-width images with number text */}
          <div className="mySlides fade">
            <div className="numbertext">1 / 6</div>
            <img
              className="image-format"
              src={"https://www.w3schools.com/howto/img_woods_wide.jpg"}
              style={{ width: "100%" }}
            />
          </div>
          <div className="mySlides fade">
            <div className="numbertext">2 / 6</div>
            <img
              className="image-format"
              src={"https://www.w3schools.com/howto/img_5terre_wide.jpg"}
              style={{ width: "100%" }}
            />
          </div>
          <div className="mySlides fade">
            <div className="numbertext">3 / 6</div>
            <img
              className="image-format"
              src={"https://www.w3schools.com/howto/img_mountains_wide.jpg"}
              style={{ width: "100%" }}
            />
          </div>
          <div className="mySlides fade">
            <div className="numbertext">4 / 6</div>
            <img
              className="image-format"
              src={"https://www.w3schools.com/howto/img_lights_wide.jpg"}
              style={{ width: "100%" }}
            />
          </div>
          <div className="mySlides fade">
            <div className="numbertext">5 / 6</div>
            <img
              className="image-format"
              src={"https://www.w3schools.com/howto/img_nature_wide.jpg"}
              style={{ width: "100%" }}
            />
          </div>
          <div className="mySlides fade">
            <div className="numbertext">6 / 6</div>
            <img
              className="image-format"
              src={"https://www.w3schools.com/howto/img_snow_wide.jpg"}
              style={{ width: "100%" }}
            />
          </div>
          {/* Next and previous buttons */}
          <a className="prev" onclick="plusSlides(-1)">
            ❮
          </a>
          <a className="next" onclick="plusSlides(1)">
            ❯
          </a>
          {/* Image text */}
          <div className="caption-container">
            <p id="caption" />
          </div>
          {/* The dots/circles */}
          <div style={{ textAlign: "center" }}>
            <span className="dot" onclick="currentSlide(1)" />
            <span className="dot" onclick="currentSlide(2)" />
            <span className="dot" onclick="currentSlide(3)" />
            <span className="dot" onclick="currentSlide(4)" />
            <span className="dot" onclick="currentSlide(5)" />
            <span className="dot" onclick="currentSlide(6)" />
          </div>
          {/* Thumbnail images */}
          <div className="row">
            <div className="mini-image">
              <img
                className="image-format demo cursor"
                src={"https://www.w3schools.com/howto/img_woods_wide.jpg"}
                style={{ width: "100%" }}
                onclick="currentSlide(1)"
                alt=""
              />
            </div>
            <div className="mini-image ">
              <img
                className="image-format demo cursor"
                src={"https://www.w3schools.com/howto/img_5terre_wide.jpg"}
                style={{ width: "100%" }}
                onclick="currentSlide(2)"
                alt=""
              />
            </div>
            <div className="mini-image">
              <img
                className="image-format demo cursor"
                src={"https://www.w3schools.com/howto/img_mountains_wide.jpg"}
                style={{ width: "100%" }}
                onclick="currentSlide(3)"
                alt=""
              />
            </div>
            <div className="mini-image">
              <img
                className="image-format demo cursor"
                src={"https://www.w3schools.com/howto/img_lights_wide.jpg"}
                style={{ width: "100%" }}
                onclick="currentSlide(4)"
                alt=""
              />
            </div>
            <div className="mini-image">
              <img
                className="image-format demo cursor"
                src={"https://www.w3schools.com/howto/img_nature_wide.jpg"}
                style={{ width: "100%" }}
                onclick="currentSlide(5)"
                alt=""
              />
            </div>
            <div className="mini-image">
              <img
                className="image-format demo cursor"
                src={"https://www.w3schools.com/howto/img_snow_wide.jpg"}
                style={{ width: "100%" }}
                onclick="currentSlide(6)"
                alt=""
              />
            </div>
          </div>
        </div>
      </div>
    );
  }
}
