import React, { Component } from "react";
import "./style.css";
import _ from "lodash";

function importAll(r) {
  return r.keys().map(r);
}

const images = importAll(
  require.context("../../Asset/apple/iphone13", false, /\.(png|jpe?g|svg)$/)
);

const myImage = (name) => {
  let img = null;
  images.map((data) => {
    if (data.default.includes(name)) img = data.default;
  });
  return img;
};

