const total = 6;
export default class index extends Component {
  state = { slideIndex: 0 };
 plusSlides(n) {
  this.setState({slideIndex:this.state.slideIndex+n});
}

  render() {
    return (
      <div>
        <div className="box">
          {/* Full-width images with number text */}
          {
            (function (){
              var result =[];
              for (var i = 0; i < total; i++){
                if (i==this.state.slideIndex){
                  result.push(<div className="mySlides fade">
                  <div className="numbertext"> {i+1}/ {total}</div>
                  <img
                    className="image-format"
                    className="show"
                    src={myImage(i)}
                    style={{ width: "100%"}}
                  />
                </div>);
                }else{
                  result.push(<div className="mySlides fade">
                  <div className="numbertext"> {i+1}/ {total}</div>
                  <img
                    className="image-format"
                    className="hidden"
                    src={myImage(i)}
                    style={{ width: "100%"}}
                  />
                </div>);
                }

              }
            }).bind(this)()
          }


          {/* Next and previous buttons */}
          <a className="prev" onclick={this.plusSlides(-1)}>
            ❮
          </a>
          <a className="next" onclick={this.plusSlides(1)}>
            ❯
          </a>
          {/* Image text */}
          <div className="caption-box">
            <p id="caption" />
          </div>
          {/* The dots/circles */}
          <div style={{ textAlign: "center" }}>
            {function () {
              // body
              var result = [];
              for (var i = 0; i < total; i++) {
                if (i == this.state.slideIndex) {
                  result.push(<span className="dot" className="active" />);
                } else result.push(<span className="dot" />);
              }

              return result;
            }.bind(this)()}
          </div>
          {/* Thumbnail images */}
          <div className="row">
            {function () {
              var result = [];
              for (var i = 0; i < total; i++) {
                if (i == this.state.slideIndex) {
                  result.push(
                    <div className="mini-image">
                      <img
                        className="image-format demo cursor"
                        className="active"
                        src={myImage(i)}
                        style={{ width: "100%" }}
                        alt=""
                      />
                    </div>
                  );
                } else
                  result.push(
                    <div className="mini-image">
                      <img
                        className="image-format demo cursor"
                        src={myImage(i)}
                        style={{ width: "100%" }}
                        alt=""
                      />
                    </div>
                  );
              }

              return result;
            }.bind(this)()}
            {/* Thumbnail images */}
          </div>
          {/* <div className="row">
            {_.times(3, (i) => (
              <div className="mini-image">
                <img
                  className="image-format demo cursor"
                  src={myImage(i)}
                  style={{ width: "100%" }}
                  alt=""
                />
              </div>
            ))}
          </div> */}
        </div>
      </div>
    );
  }
}
