import React, { Component } from "react";
import Modal from "../MyModal/index";
import * as hoadonApi from "../../apis/billing";
import * as myToast from "../../../Common/helper/toastHelper";
import * as detailBill from "../../apis/detailBill";
export default class AddBill extends Component {
  state = {
    chitiethoadon: [],
  };
  themChiTietHoaDon() {
    let newchitiethoadon = this.state.chitiethoadon;
    newchitiethoadon.push({});
    this.setState({ chitiethoadon: newchitiethoadon });
  }
  async componentDidMount() {
    if (this.props.bill !== undefined)
      await detailBill
        .detailByBillId(this.props.bill.sohoadon)
        .then((success) => {
          this.setState({ chitiethoadon: success.data.value.$values });
        })
        .catch((error) => {});
  }
  async saveChanges() {
    let makhachhang = document.getElementById("makhachhang").value;
    let makhuyenmai = document.getElementById("makhuyenmai").value;
    let ngayhoadon = document.getElementById("ngayhoadon").value;
    let manhanvien = document.getElementById("manhanvien").value;
    let tongtien = document.getElementById("tongtien").value;

    let imei = document.getElementsByName("imei");
    let tien = document.getElementsByName("tien");
    let chitiethoadon = [];
    if (imei !== undefined)
      for (let i = 0; i < imei.length; i++) {
        let temp = { imei: imei[i].value, tien: tien[i].value };
        chitiethoadon.push(temp);
      }

    let hoadon = {
      makhachhang: parseInt(makhachhang),
      makhuyenmai: parseInt(makhuyenmai),
      ngayhoadon: ngayhoadon,
      manhanvien: parseInt(manhanvien),
      tongtien: parseInt(tongtien),
      chitiethoadon: chitiethoadon,
    };

    if (this.props.bill !== undefined) {
      await hoadonApi
        .addBilling(hoadon)
        .then((success) => {
          if (success.status === 200) {
            myToast.toastSucces("Thêm mới hoá đơn thành công");
            this.props.handleSave();
          } else myToast.toastError("Thêm mới thất bại");
        })
        .catch((error) => {
          myToast.toastError("Thêm mới thất bại");
        });
    } else {
      await hoadonApi
        .updateBilling(hoadon)
        .then((success) => {
          if (success.status === 200) {
            myToast.toastSucces("Thêm mới hoá đơn thành công");
            this.props.handleSave();
          } else myToast.toastError("Cập nhật thất bại");
        })
        .catch((error) => {
          myToast.toastError("Cập nhật thất bại");
        });
    }
    // this.props.handleSave();
  }
  render() {
    return (
      <Modal
        handleClose={this.props.handleClose}
        handleSave={this.saveChanges.bind(this)}
        title={"Hoá đơn"}
      >
        <div className="row mb-1">
          <div className="col-xs-6 col-sm-6 col-md-6 col-lg-6">Số hoá đơn</div>
          <div className="col-xs-6 col-sm-6 col-md-6 col-lg-6 ">
            <p>{this.props.bill !== undefined && this.props.bill.sohoadon}</p>
          </div>
        </div>
        <div className="row mb-1">
          <div className="col-xs-6 col-sm-6 col-md-6 col-lg-6">
            Mã khách hàng
          </div>
          <div className="col-xs-6 col-sm-6 col-md-6 col-lg-6 ">
            <input
              className="form-control border "
              id="makhachhang"
              placeholder=""
              value={
                this.props.bill !== undefined && this.props.bill.makhachhang
              }
            ></input>
          </div>
        </div>
        <div className="row mb-1">
          <div className="col-xs-6 col-sm-6 col-md-6 col-lg-6">
            Mã khuyến mãi
          </div>
          <div className="col-xs-6 col-sm-6 col-md-6 col-lg-6 ">
            <input
              className="form-control border"
              id="makhuyenmai"
              placeholder=""
              value={
                this.props.bill !== undefined && this.props.bill.makhuyenmai
              }
            ></input>
          </div>
        </div>
        <div className="row mb-1">
          <div className="col-xs-6 col-sm-6 col-md-6 col-lg-6">
            Ngày hoá đơn
          </div>
          <div className="col-xs-6 col-sm-6 col-md-6 col-lg-6 ">
            <input
              className="form-control border"
              type="date"
              id="ngayhoadon"
              name="trip-start"
              value={
                this.props.bill !== undefined && this.props.bill.ngayhoadon
              }
            ></input>
          </div>
        </div>
        <div className="row mb-1">
          <div className="col-xs-6 col-sm-6 col-md-6 col-lg-6">
            Mã nhân viên
          </div>
          <div className="col-xs-6 col-sm-6 col-md-6 col-lg-6 ">
            <input
              className="form-control border"
              id="manhanvien"
              placeholder=""
              value={
                this.props.bill !== undefined && this.props.bill.manhanvien
              }
            ></input>
          </div>
        </div>
        <div className="row mb-1">
          <div className="col-xs-6 col-sm-6 col-md-6 col-lg-6">Tổng tiền</div>
          <div className="col-xs-6 col-sm-6 col-md-6 col-lg-6 ">
            <input
              className="form-control border"
              id="tongtien"
              placeholder=""
              value={this.props.bill !== undefined && this.props.bill.tongtien}
            ></input>
          </div>
        </div>
        <hr></hr>

        {function () {
          let result = [];
          for (var i = 0; i < this.state.chitiethoadon.length; i++) {
            result.push(
              <div>
                <div className="row mb-1">
                  <div className="col-xs-6 col-sm-6 col-md-6 col-lg-6">
                    IMEI
                  </div>
                  <div className="col-xs-6 col-sm-6 col-md-6 col-lg-6 ">
                    <input
                      className="form-control border"
                      id="imei"
                      name="imei"
                      placeholder=""
                      value={this.state.chitiethoadon[i].imei}
                    ></input>
                  </div>
                </div>
                <div className="row mb-1">
                  <div className="col-xs-6 col-sm-6 col-md-6 col-lg-6">
                    Tiền
                  </div>
                  <div className="col-xs-6 col-sm-6 col-md-6 col-lg-6 ">
                    <input
                      className="form-control border"
                      id="tien"
                      name="tien"
                      placeholder=""
                      value={this.state.chitiethoadon[i].tien}
                    ></input>
                  </div>
                </div>
                <hr></hr>
              </div>
            );
          }
          return result;
        }.bind(this)()}

        <div className="row mb-1">
          <button
            className="btn btn-info"
            onClick={this.themChiTietHoaDon.bind(this)}
          >
            Thêm chi tiết hoá đơn
          </button>
        </div>
      </Modal>
    );
  }
}
