import React, { Component } from "react";
import "./style.css";
import _ from "lodash";

function importAll(r) {
  return r.keys().map(r);
}

const images = importAll(
  require.context("../../Asset/apple/iphone13", false, /\.(png|jpe?g|svg)$/)
);

const myImage = (name) => {
  let img = null;
  images.map((data) => {
    if (data.default.includes(name)) img = data.default;
  });
  return img;
};

const total = 6;
export default class index extends Component {
  state ={slideIndex:0}
  nextSlides=() =>{
    var n=this.state.slideIndex+1;
    if (n==total) n=0;
    this.setState({slideIndex:n});
  }
  preSlides=()=> {
    var n=this.state.slideIndex-1;
    if (n==-1) n=total-1;
    this.setState({slideIndex:n});

  }
  click =(n)=>{
    this.setState({slideIndex:n});
  }
  render() {
    return (
        <div className="box">

          {(function () {
            console.log("index=" + this.state.slideIndex);
            var result = [];
            for (var i = 0; i < total; i++) {
              result.push(
                <div
                  className=" fade"
                  className={i === this.state.slideIndex ? "show" : "hidden"}
                >
                  <div className="numbertext">
                    {i}/{total}
                  </div>
                  <img className="image-format" src={myImage(i)} />
                </div>
              );
            }
            return result;
          }.bind(this))()}

          <a className="prev" onClick={this.preSlides}>
            ❮
          </a>
          <a className="next" onClick={this.nextSlides}>
            ❯
          </a>
          {/* Image text */}
          <div className="caption-box">
            <p id="caption" />
          </div>
          {/* The dots/circles */}
          <div style={{ textAlign: "center" }}>
            {(function () {
              // body
              var result = [];
              for (var i = 0; i < total; i++) {
                if (i == this.state.slideIndex) {
                  result.push(<span className="dot" className="active" />);
                } else result.push(<span className="dot" />);
              }

              return result;
            }.bind(this))()}
          </div>
          {/* Thumbnail images */}
          <div className="row">
            {(function () {
              var result = [];
              for (var i = 0; i < 5; i++) {
                if (i == this.state.slideIndex) {
                  result.push(
                    <div className="mini-image">
                      <img
                        className="image-format demo cursor"
                        className="active"
                        src={myImage(i)}
                        style={{ width: "100%" }}
                        alt=""
                      />
                    </div>
                  );
                }
                result.push(
                  <div className="mini-image">
                    <img
                      className="image-format demo cursor"
                      src={myImage(i)}
                      style={{ width: "100%" }}
                      alt=""
                      value={i}
                      onClick={() =>this.click(i)}
                    />
                  </div>
                );
              }

              return result;
            }.bind(this))()}
            {/* Thumbnail images */}
          </div>
          {/* <div className="row">
            {_.times(3, (i) => (
              <div className="mini-image">
                <img
                  className="image-format demo cursor"
                  src={myImage(i)}
                  style={{ width: "100%" }}
                  alt=""
                />
              </div>
            ))}
          </div> */}
        </div>
    )
  }
}
