//   }
//   for (i = 0; i < dots.length; i++) {
//     dots[i].className = dots[i].className.replace(" active", "");
//   }
//   slides[slideIndex - 1].style.display = "block";
//   demo[slideIndex - 1].className += " active";
//   captionText.innerHTML = demo[slideIndex - 1].alt;
//   dots[slideIndex - 1].className += " active";
// }

const total = 6;
export default class index extends Component {
  state = { slideIndex: 0 };

  render() {
    return (
      <div>
        <div className="box">
          {/* Full-width images with number text */}
          <div className="mySlides fade">
            <div className="numbertext">1 / 6</div>
            <img
              className="image-format"
              src={myImage(1)}
              style={{ width: "100%" }}
            />
          </div>
          <div className="mySlides fade">
            <div className="numbertext">2 / 6</div>
            <img
              className="image-format"
              src={myImage(2)}
              style={{ width: "100%" }}
            />
          </div>
          <div className="mySlides fade">
            <div className="numbertext">3 / 6</div>
            <img
              className="image-format"
              src={myImage(3)}
              style={{ width: "100%" }}
            />
          </div>
          <div className="mySlides fade">
            <div className="numbertext">4 / 6</div>
            <img
              className="image-format"
              src={myImage(4)}
              style={{ width: "100%" }}
            />
          </div>
          <div className="mySlides fade">
            <div className="numbertext">5 / 6</div>
            <img
              className="image-format"
              src={myImage(5)}
              style={{ width: "100%" }}
            />
          </div>
          <div className="mySlides fade">
            <div className="numbertext">6 / 6</div>
            <img
              className="image-format"
              src={myImage(6)}
              style={{ width: "100%" }}
            />
          </div>
          {/* Next and previous buttons */}
          <a className="prev" onclick={"this.plusSlides(-1)"}>
            ❮
          </a>
          <a className="next" onclick={"this.plusSlides(1)"}>
            ❯
          </a>
          {/* Image text */}
          <div className="caption-box">
            <p id="caption" />
          </div>
          {/* The dots/circles */}
          <div style={{ textAlign: "center" }}>
            {(function () {
              // body
              var result = [];
              for (var i = 0; i < total; i++) {

                result.push(<span className="dot" />
                );
              }

              return result;
            })()}
          </div>
          {/* Thumbnail images */}
          <div className="row">
            {(function () {
              var result = [];
              for (var i = 0; i < total; i++) {
                if (i==i){
                  result.push(
                    <div className="mini-image">
                      <img
                        className="image-format demo cursor active"             
                        src={myImage(i)}
                        style={{ width: "100%" }}
                        alt=""
                      />
                    </div>
                  );
                }else
                result.push(
                  <div className="mini-image">
                    <img
                      className="image-format demo cursor"                
                      src={myImage(i)}
                      style={{ width: "100%" }}
                      alt=""
                    />
                  </div>
                );
              }

              return result;
            })()}
            {/* Thumbnail images */}
          </div>
          {/* <div className="row">
            {_.times(3, (i) => (
              <div className="mini-image">
                <img
                  className="image-format demo cursor"
                  src={myImage(i)}
                  style={{ width: "100%" }}
                  alt=""
                />
              </div>
            ))}
          </div> */}


        </div>
      </div>
    );
  }
}
