//     slideIndex = slides.length;
//   }
//   for (i = 0; i < slides.length; i++) {
//     slides[i].style.display = "none";
//   }
//   for (i = 0; i < demo.length; i++) {
//     demo[i].className = demo[i].className.replace(" active", "");
//   }
//   for (i = 0; i < dots.length; i++) {
//     dots[i].className = dots[i].className.replace(" active", "");
//   }
//   slides[slideIndex - 1].style.display = "block";
//   demo[slideIndex - 1].className += " active";
//   captionText.innerHTML = demo[slideIndex - 1].alt;
//   dots[slideIndex - 1].className += " active";
// }

const total = 6;
export default class index extends Component {
  state = { slideIndex: 1 };

  // Next/previous controls
  plusSlides(n) {
    let { slideIndex } = this.state;
    slideIndex += n;
    this.setState({ slideIndex: slideIndex });
    this.showSlides(slideIndex);
  }
  // Thumbnail image controls
  currentSlide(n) {
    let { slideIndex } = this.state;
    slideIndex = n;
    this.setState({ slideIndex: slideIndex });
    this.showSlides((slideIndex = n));
  }

  showSlides(n) {
    var i;
    var slides = document.getElementsByClassName("mySlides");
    var demo = document.getElementsByClassName("demo");
    var captionText = document.getElementById("caption");
    var dots = document.getElementsByClassName("dot");
    let { slideIndex } = this.state;
    if (n > slides.length) {
      slideIndex = 1;
    }
    if (n < 1) {
      slideIndex = slides.length;
    }
    for (i = 0; i < slides.length; i++) {
      slides[i].style.display = "none";
    }
    for (i = 0; i < demo.length; i++) {
      demo[i].className = demo[i].className.replace(" active", "");
    }
    for (i = 0; i < dots.length; i++) {
      dots[i].className = dots[i].className.replace(" active", "");
    }
    // slides[slideIndex - 1].display = "block";
    demo[slideIndex - 1].className += " active";
    captionText.innerHTML = demo[slideIndex - 1].alt;
    dots[slideIndex - 1].className += " active";
    this.setState({ slideIndex: slideIndex });
  }
  render() {
    return (
      <div>
        <div className="box">
          {/* Full-width images with number text */}
          <div className="mySlides fade">
            <div className="numbertext">1 / 6</div>
            <img
              className="image-format"
              src={myImage(1)}
              style={{ width: "100%" }}
            />
          </div>
          <div className="mySlides fade">
            <div className="numbertext">2 / 6</div>
            <img
              className="image-format"
              src={myImage(2)}
              style={{ width: "100%" }}
            />
          </div>
          <div className="mySlides fade">
            <div className="numbertext">3 / 6</div>
            <img
              className="image-format"
              src={myImage(3)}
              style={{ width: "100%" }}
            />
          </div>
          <div className="mySlides fade">
            <div className="numbertext">4 / 6</div>
            <img
              className="image-format"
              src={myImage(4)}
              style={{ width: "100%" }}
            />
          </div>
          <div className="mySlides fade">
            <div className="numbertext">5 / 6</div>
            <img
              className="image-format"
              src={myImage(5)}
              style={{ width: "100%" }}
            />
          </div>
          <div className="mySlides fade">
            <div className="numbertext">6 / 6</div>
            <img
              className="image-format"
              src={myImage(6)}
              style={{ width: "100%" }}
            />
          </div>
          {/* Next and previous buttons */}
          <a className="prev" onclick={this.plusSlides(-1)}>
            ❮
          </a>
          <a className="next" onclick={this.plusSlides(1)}>
            ❯
          </a>
          {/* Image text */}
          <div className="caption-box">
            <p id="caption" />
          </div>
          {/* The dots/circles */}
          <div style={{ textAlign: "center" }}>
            {(function () {
              // body
              var result=null;
              for (var i = 0; i <total;i++){
                  result += <span className="dot" onclick={this.currentSlide(i)} />
              }
              return result;
            })()}
          </div>
          {/* Thumbnail images */}
          <div className="row">
            <div className="mini-image">
              <img
                className="image-format demo cursor"
                src={myImage(1)}
                style={{ width: "100%" }}
                onclick={this.currentSlide(1)}
                alt=""
              />
            </div>
            <div className="mini-image ">
              <img
                className="image-format demo cursor"
                src={myImage(2)}
                style={{ width: "100%" }}
                onclick={this.currentSlide(2)}
                alt=""
              />
            </div>
            <div className="mini-image">
              <img
                className="image-format demo cursor"
                src={myImage(3)}
                style={{ width: "100%" }}
                onclick={this.currentSlide(3)}
                alt=""
              />
            </div>
            <div className="mini-image">
              <img
                className="image-format demo cursor"
                src={myImage(4)}
                style={{ width: "100%" }}
                onclick={this.currentSlide(4)}
                alt=""
              />
            </div>
            <div className="mini-image">
              <img
                className="image-format demo cursor"
                src={myImage(5)}
                style={{ width: "100%" }}
                onclick={this.currentSlide(5)}
                alt=""
              />
            </div>
            <div className="mini-image">
              <img
                className="image-format demo cursor"
                src={myImage(6)}
                style={{ width: "100%" }}
                onclick={this.currentSlide(6)}
                alt=""
              />
            </div>
          </div>
        </div>
      </div>
    );
  }
}
