import React, { Component } from "react";
import Modal from "../MyModal/index";

export default class AddBill extends Component {
  state = {
    chitiethoadon: 0,
  };
  themChiTietHoaDon(){
      this.setState({chitiethoadon:this.state.chitiethoadon+1});
  }
  async saveChanges(){
    let makhachhang = document.getElementById("makhachhang").value;
    let makhuyenmai = document.getElementById("makhuyenmai").value;
    let ngayhoadon = document.getElementById("ngayhoadon").value;
    let manhanvien = document.getElementById("manhanvien").value;
    let tongtien = document.getElementById("tongtien").value;

    let imei = document.getElementById("imei");
    let tien = document.getElementById("tien");
    let chitiethoadon = [];
    if (imei!==undefined)
    for (let i = 0; i <imei.length; i++) {
        let temp = {imei:imei[i], tien: tien[i] };
        chitiethoadon.push(temp);
    }

    this.props.handleSave();
  }
  render() {
    return (
      <Modal
        handleClose={this.props.handleClose}
        handleSave={this.saveChanges.bind(this)}
        title={"Hoá đơn"}
      >
        <div className="row mb-1">
          <div className="col-xs-6 col-sm-6 col-md-6 col-lg-6">Số hoá đơn</div>
          <div className="col-xs-6 col-sm-6 col-md-6 col-lg-6 "></div>
        </div>
        <div className="row mb-1">
          <div className="col-xs-6 col-sm-6 col-md-6 col-lg-6">
            Mã khách hàng
          </div>
          <div className="col-xs-6 col-sm-6 col-md-6 col-lg-6 ">
            <input
              className="form-control border "
              id="makhachhang"
              placeholder=""
            ></input>
          </div>
        </div>
        <div className="row mb-1">
          <div className="col-xs-6 col-sm-6 col-md-6 col-lg-6">
            Mã khuyến mãi
          </div>
          <div className="col-xs-6 col-sm-6 col-md-6 col-lg-6 ">
            <input
              className="form-control border"
              id="makhuyenmai"
              placeholder=""
            ></input>
          </div>
        </div>
        <div className="row mb-1">
          <div className="col-xs-6 col-sm-6 col-md-6 col-lg-6">
            Ngày hoá đơn
          </div>
          <div className="col-xs-6 col-sm-6 col-md-6 col-lg-6 ">
            <input
              className="form-control border"
              type="date"
              id="ngayhoadon"
              name="trip-start"
            ></input>
          </div>
        </div>
        <div className="row mb-1">
          <div className="col-xs-6 col-sm-6 col-md-6 col-lg-6">
            Mã nhân viên
          </div>
          <div className="col-xs-6 col-sm-6 col-md-6 col-lg-6 ">
            <input
              className="form-control border"
              id="manhanvien"
              placeholder=""
            ></input>
          </div>
        </div>
        <div className="row mb-1">
          <div className="col-xs-6 col-sm-6 col-md-6 col-lg-6">Tổng tiền</div>
          <div className="col-xs-6 col-sm-6 col-md-6 col-lg-6 ">
            <input
              className="form-control border"
              id="tongtien"
              placeholder=""
            ></input>
          </div>
        </div>
        <hr></hr>

        {function () {
          let result = [];
          for (var i = 0; i <this.state.chitiethoadon; i++){
              result.push( 
                <div>
                  <div className="row mb-1">
                    <div className="col-xs-6 col-sm-6 col-md-6 col-lg-6">
                      IMEI
                    </div>
                    <div className="col-xs-6 col-sm-6 col-md-6 col-lg-6 ">
                      <input
                        className="form-control border"
                        id="imei"
                        placeholder=""
                      ></input>
                    </div>
                  </div>
                  <div className="row mb-1">
                    <div className="col-xs-6 col-sm-6 col-md-6 col-lg-6">
                      Tiền
                    </div>
                    <div className="col-xs-6 col-sm-6 col-md-6 col-lg-6 ">
                      <input
                        className="form-control border"
                        id="tien"
                        placeholder=""
                      ></input>
                    </div>
                  </div>
                  <hr></hr>
                </div>
              );
            }   
          return result;
        }.bind(this)()}

        <div className="row mb-1">
          <button className="btn btn-info" onClick={this.themChiTietHoaDon.bind(this)}>Thêm chi tiết hoá đơn</button>
        </div>
      </Modal>
    );
  }
}
