import React, { Component } from "react";
import "./style.css";
import _ from "lodash";

function importAll(r) {
  return r.keys().map(r);
}

const images = importAll(
  require.context("../../Asset/apple/iphone13", false, /\.(png|jpe?g|svg)$/)
);

const myImage = (name) => {
  let img = null;
  images.map((data) => {
    if (data.default.includes(name)) img = data.default;
  });
  return img;
};

const total = 6;
export default class index extends Component {
  constructor(props) {
    super(props);
    this.state = {slideIndex: 0};

    // This binding is necessary to make `this` work in the callback
  }
  nextSlides() {
    this.click(this.state.slideIndex+1);
    console.log("next");
  }
  preSlides() {
    console.log("pre");
    this.click(this.state.slideIndex-1);

  }
  click=(n) => {
    console.log("click");
    this.setState({ slideIndex: n });
    // this.forceUpdate();
  }

  render() {
    return (
      <div>
        <div className="box">
          {/* Full-width images with number text */}

          {(function () {
            console.log("index=" + this.state.slideIndex);
            var result = [];
            for (var i = 0; i < total; i++) {
              result.push(
                <div
                  className=" fade"
                  className={i == this.state.slideIndex ? "show" : "hidden"}
                >
                  <div className="numbertext">
                    {" "}
                    {i}/{total}
                  </div>
                  <img className="image-format" src={myImage(1)} />
                </div>
              );
            }
            return result;
          }.bind(this))()}

          {/* Next and previous buttons */}
          <a className="prev" onclick={this.preSlides}>
            ❮
          </a>
          <a className="next" onclick={this.nextSlides}>
            ❯
          </a>
          {/* Image text */}
          <div className="caption-box">
            <p id="caption" />
          </div>
          {/* The dots/circles */}
          <div style={{ textAlign: "center" }}>
            {(function () {
              // body
              var result = [];
              for (var i = 0; i < total; i++) {
                if (i == this.state.slideIndex) {
                  result.push(<span className="dot" className="active" />);
                } else result.push(<span className="dot" />);
              }

              return result;
            }.bind(this))()}
          </div>
          {/* Thumbnail images */}
          <div className="row">
            {(function () {
              var result = [];
              for (var i = 0; i < total; i++) {
                if (i == this.state.slideIndex) {
                  result.push(
                    <div className="mini-image">
                      <img
                        className="image-format demo cursor"
                        className="active"
                        src={myImage(i)}
                        style={{ width: "100%" }}
                        alt=""
                      />
                    </div>
                  );
                }
                result.push(
                  <div className="mini-image">
                    <img
                      className="image-format demo cursor"
                      src={myImage(i)}
                      style={{ width: "100%" }}
                      alt=""
                      onclick={() =>this.click(i)}
                    />
                  </div>
                );
              }

              return result;
            }.bind(this))()}
            {/* Thumbnail images */}
          </div>
          {/* <div className="row">
            {_.times(3, (i) => (
              <div className="mini-image">
                <img
                  className="image-format demo cursor"
                  src={myImage(i)}
                  style={{ width: "100%" }}
                  alt=""
                />
              </div>
            ))}
          </div> */}
          <button onclick={() =>this.click(3)}>Test</button>
        </div>
      </div>
    );
  }
}
