import React, { Component } from "react";
import * as productApi from "../../apis/product";
import * as customerApi from "../../apis/customer";

export default class BillItem extends Component {
  state = {product: undefined, customer: undefined};
  async componentDidMount() {
    let product=  undefined;
    let customer=   undefined;
    await productApi.findProductById(this.props.bill.sohoadon)
    .then((success) => {
      product= success.data.value;
    })
    .catch((error) => {
    });

    await customerApi.findCustomerByBillId(this.props.bill.sohoadon)
    .then((success) => {
      customer= success.data.value;
    })
    .catch((error) => {
    });

    this.setState({product: product, customer: customer});
  }
  render() {
    return (
      <li className="list-group-item border-0 d-flex p-4 bg-gray-100 border-radius-lg">
        <div className="d-flex flex-column">
          <h6 className="text-sm">{this.state.product!==undefined & this.state.product!==null  && this.state.product.tenkhachhang}</h6>
          <span className="mb-2 text-xs">
            Số điện thoại:{" "}
            <span className="text-dark font-weight-bold ms-sm-2">
            {this.state.product!==undefined & this.state.product!==null  && this.state.product.sodienthoai}
            </span>
          </span>
          <span className="mb-2 text-xs">
            Email:{" "}
            <span className="text-dark ms-sm-2 font-weight-bold">
            {this.state.product!==undefined & this.state.product!==null  && this.state.product.email}
            </span>
          </span>
          <span className="text-xs">
            Tên sản phẩm: {" "}
            <span className="text-dark ms-sm-2 font-weight-bold">
            {this.state.product!==undefined & this.state.product!==null  && this.state.product.tensanpham}
              {"    "}
            </span>
            {"    "}Đơn giá: {" "}
            <span className="text-dark ms-sm-2 font-weight-bold">
            {this.state.product!==undefined & this.state.product!==null  && this.state.product.dongia}
              {"    "}
            </span>
            {"    "}Số lượng: {" "}
            <span className="text-dark ms-sm-2 font-weight-bold">
            soluong
            </span>
          </span>  
          <span className=" mt-2 text-xs">
            Ngày hoá đơn: {" "}
            <span className="text-dark ms-sm-2 font-weight-bold">
            {this.props.bill.ngayhoadon}
            </span>
          </span>      
          <span className=" mt-2 text-xs">
            Tổng tiền: {" "}
            <span className="text-dark ms-sm-2 font-weight-bold">
            {this.props.bill.tongtien}
            </span>
          </span>
        </div>
        <div className="ms-auto text-end">
          <a
            className="btn btn-link text-danger text-gradient px-3 mb-0"
            href="javascript:;"
          >
            <i class="bi bi-trash"></i>
            {"  "}Xoá
          </a>
          <a className="btn btn-link text-dark px-3 mb-0" href="javascript:;">
            <i class="bi bi-pencil-square"></i>
            {"  "}Sửa
          </a>
        </div>
      </li>
    );
  }
}
