import React, { Component } from "react";
import { Route, Link } from "react-router-dom";
const table = [
  {
    name: "Tổng quan",
    to: "/admin",
  },
  {
    name: "Hoá đơn",
    to: "/admin/billing",
  },
  {
    name: "Khách hàng",
    to: "/admin/customer",
  },
  {
    name: "Sản phẩm",
    to: "/admin/product",
  },
];
export default class index extends Component {
  state = { active: 0 };
  render() {
    return (
      <aside
        className="sidenav navbar navbar-vertical navbar-expand-xs border-0 border-radius-xl my-3 fixed-start ms-3   bg-gradient-dark"
        id="sidenav-main"
      >
        <div className="sidenav-header">
          <i
            className="fas fa-times p-3 cursor-pointer text-white opacity-5 position-absolute end-0 top-0 d-none d-xl-none"
            aria-hidden="true"
            id="iconSidenav"
          />
          <a
            className="navbar-brand m-0"
            href=" https://demos.creative-tim.com/material-dashboard/pages/dashboard "
            target="_blank"
          >
            <img
              src="./assets/img/logo-ct.png"
              className="navbar-brand-img h-100"
              alt="main_logo"
            />
            <span className="ms-1 font-weight-bold text-white">
              Material Dashboard 2
            </span>
          </a>
        </div>
        <hr className="horizontal light mt-0 mb-2" />
        <div
          className="collapse navbar-collapse  w-auto  max-height-vh-100"
          id="sidenav-collapse-main"
        >
          <ul className="navbar-nav">
            {(() => {
              var result = null;
              result = table.map((value, key) => {
                return (
                  <Route
                    path={value.to}
                    exact={false}
                    children={({ match }) => {
                      return (
                        <li className="nav-item">
                          <Link
                            className={
                              match
                                ? "nav-link text-white active bg-gradient-primary"
                                : "nav-link text-white bg-gradient-primary"
                            }
                          >
                            <div className="text-white text-center me-2 d-flex align-items-center justify-content-center">
                              <i class="icon-dashboard"></i>
                            </div>
                            <span className="nav-link-text ms-1">
                              {value.name}
                            </span>
                          </Link>
                        </li>
                      );
                    }}
                  ></Route>
                );
              });
              return result;
            })()}
          </ul>
        </div>
        <div className="sidenav-footer position-absolute w-100 bottom-0 ">
          <div className="mx-3">
            <a
              className="btn bg-gradient-primary mt-4 w-100"
              href="https://www.creative-tim.com/product/material-dashboard-pro?ref=sidebarfree"
              type="button"
            >
              Upgrade to pro
            </a>
          </div>
        </div>
      </aside>
    );
  }
}
